        [TestMethod]
        public void TestFindEvenIndex()
        {
            Assert.AreEqual(3, new int[] { 1, 2, 3, 4, 3, 2, 1}.FindEvenIndex());
        }

        [TestMethod]
        public void TestFindEvenIndexSkumt()
        {
            Assert.AreEqual(3, new int[] { 20, 10, 30, 10, 10, 15, 35 }.FindEvenIndex());
        }

        [TestMethod]
        public void TestFindEvenIndexNotFound()
        {
            Assert.AreEqual(-1, new int[] { 1, 2, 3, 4, 5, 6 }.FindEvenIndex());
        }
        [TestMethod]
        public void TestFindEvenIndex3()
        {
            Assert.AreEqual(1, new int[] { 1, 100, 50, -51, 1, 1 }.FindEvenIndex());
        }

